import React,{Component} from 'react';
import Card from './CardUI';
import img1 from '../assets/index.jpeg';
import img2 from '../assets/index1.jpeg';
import img3 from '../assets/index2.jpeg';
import img4 from '../assets/index3.jpeg';
import img5 from '../assets/index4.jpeg';
import img6 from '../assets/index5.jpeg';
import img7 from '../assets/index6.jpeg';
import img8 from '../assets/index7.jpeg';
import img9 from '../assets/index8.jpeg';
//import img7 from '../assets/index6.jpeg';
//import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
  },
  gridList: {
    flexWrap: 'nowrap'}
}));

const tileData = [
{
  img: './assets/index.jpg',
  title: 'title'
},
{
  img: './assets/index1.jpg',
  title: 'title'
},
{
  img: 'images/image3.jpg',
  title: 'title'
}
];

export default function SingleLineGridList() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <GridList className={classes.gridList} cols={2.5}>
        {tileData.map((tile) => (
          <GridListTile key={tile.img}>
            <img src={tile.img} alt={tile.title} />            
            <GridListTileBar
              title={tile.title}
              />
          </GridListTile>
        ))}
      </GridList>
    </div>
  );
}
