import React,{Component} from 'react';
import Card from './CardUI';
import img1 from '../assets/index.jpeg';
import img2 from '../assets/index1.jpeg';
import img3 from '../assets/index2.jpeg';
import img4 from '../assets/index3.jpeg';
import img5 from '../assets/index4.jpeg';
import img6 from '../assets/index5.jpeg';
//import img7 from '../assets/index6.jpeg';
export class Cards extends Component{
    constructor(props) {
        super(props);
        // React Ref is created here
        this.cardRef = React.createRef();
    } 
    handleNav = (direction) => {
        if (direction === 'left') {
          this.cardRef ? (this.cardRef.current.scrollLeft -= 200) : null;
        } else {
          this.cardRef ? (this.cardRef.current.scrollLeft += 200) : null;
        }
      }   
    render(){
        return(
            <div className = "container-fluid d-flex justify-content-left">

            <div>
              <button onClick={() => this.handleNav('left')}><i className="fa fa-lg fa-chevron-left"></i></button>
            </div>
                     <div className = "row">



                    <div className = "col-md-30">

                        <Card imgsrc = {img1} title = "book1"/>

                    </div>
                    <div className = "col-md-30">
                    <Card imgsrc = {img2} title = "book2"/>

                    </div>
                    <div className = "row-md-30">
                    <Card imgsrc = {img3} title = "book3"/>
                    </div>
                    <div className = "row-md-30">
                    <Card imgsrc = {img4} title = "book4"/>
                    </div>
                    <div className = "row-md-30">
                    <Card imgsrc = {img5} title = "book5"/>
                    </div>
                    <div className = "row-md-30">
                    <Card imgsrc = {img6} title = "book6"/>
                    </div>

          <button onClick={() => this.handleNav('right')}><i className="fa fa-lg fa-chevron-right"></i></button>
                </div>
            </div>
        );
    }
}
